import { ethers } from "ethers";
import * as ReactBootStrap from "react-bootstrap";
import { useState, useEffect } from "react";
import ABIFILE from "./artifacts/contracts/BlockchainVoting.sol/BlockchainVoting.json";
import FatcVoter from "./comp/FatcVoter";
import Propsal from "./comp/Propsal";
import Set from "./comp/FatchCandi";
import Vote from "./comp/Vote";

const ABI = ABIFILE.abi;
const ContractAddress = "0xE984f31e44273844F9B313d66eBED6Eb8e73376D";

function App() {
  const [account, setAccount] = useState("");
  const [contract, setContract] = useState(null);
  const [provider, setProvider] = useState(null);
  const [isoff, setOff] = useState(false);
  const [loading, setLoading] = useState(false);

  const Dicconnect = async () => {
    if (typeof window !== "undefined") {
      if (window.localStorage.getItem("Connected")) {
        window.localStorage.removeItem("Connected");
        setOff(false);
        window.location.reload();
      }
    }
  };

  useEffect(() => {
    if (typeof window !== "undefined") {
      if (window.localStorage.getItem("Connected")) {
        Connect();
      }
    }
  }, []);

  const Connect = async () => {
    setLoading(true);
    if (typeof window.ethereum !== "undefined") {
      const account = await window.ethereum.request({
        method: "eth_requestAccounts",
      });

      setOff(true);
      window.localStorage.setItem("Connected", "injected");
      setAccount(account[0]);
      document.getElementById("connectbtn").innerHTML = account[0];

      const provider = new ethers.providers.Web3Provider(window.ethereum);
      setProvider(provider);

      const signer = provider.getSigner();
      const contract = new ethers.Contract(ContractAddress, ABI, signer);
      setContract(contract);
    }
    setLoading(false);
  };

  return (
    <div
      className="container rounded shadow-lg text-light p-4"
      style={{
        maxWidth: "1000px",
        marginTop: "30px",
        background: "linear-gradient(to right, #1f3b73, #2742ac)",
        fontFamily: "Segoe UI, Tahoma, Geneva, Verdana, sans-serif",
      }}
    >
      <p className="text-center h4 fw-bold text-warning mb-4">
        Blockchain-Based Electronic Voting System
      </p>
      <p className="text-center text-info">Please connect to Sepolia Test Network</p>

      <div className="d-flex justify-content-center mb-4 gap-3">
        <button
          onClick={Connect}
          id="connectbtn"
          className="btn btn-outline-light px-4 py-2 fw-semibold rounded-pill"
        >
          {!loading ? (
            "Connect"
          ) : (
            <ReactBootStrap.Spinner
              as="span"
              animation="border"
              size="sm"
              role="status"
              aria-hidden="true"
            />
          )}
        </button>

        <button
          onClick={Dicconnect}
          id="Dissconnectbtn"
          className="btn btn-outline-danger px-4 py-2 fw-semibold rounded-pill"
          disabled={!isoff}
        >
          Disconnect
        </button>
      </div>

      {/* Components */}
      <div className="bg-dark p-3 rounded mb-3">
        <Set contract={contract} account={account} provider={provider} />
      </div>

      <div className="bg-dark p-3 rounded mb-3">
        <Vote contract={contract} account={account} provider={provider} />
      </div>

      <div className="bg-dark p-3 rounded mb-3">
        <FatcVoter contract={contract} account={account} provider={provider} />
      </div>

      <div className="bg-dark p-3 rounded">
        <Propsal contract={contract} account={account} provider={provider} />
      </div>
    </div>
  );
}

export default App;
